package com.corebasicsex.com;

public class Arithmeticoperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=8 ,b=4;
		System.out.println(a+b);
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b);
		System.out.println(a%b);

	}

}
